import React, { useEffect, useCallback } from 'react';
import * as THREE from 'three';
import { ARButton } from 'three/examples/jsm/webxr/ARButton';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { XREstimatedLight } from 'three/examples/jsm/webxr/XREstimatedLight';
import { useParams, useNavigate } from 'react-router-dom';
import './ARView.css';

function ARView() {
  const { id } = useParams();
  const navigate = useNavigate();

  let reticle;
  let hitTestSource = null;
  let hitTestSourceRequested = false;
  let scene, camera, renderer;

  const models = [
    "./dylan_armchair_yolk_yellow.glb",
    "./ivan_armchair_mineral_blue.glb", 
    "./marble_coffee_table.glb",
    "./flippa_functional_coffee_table_w._storagewalnut.glb",
    "./frame_armchairpetrol_velvet_with_gold_frame.glb",
    "./elnaz_nesting_side_tables_brass__green_marble.glb",
  ];
  const modelScaleFactor = [0.01, 0.01, 0.005, 0.01, 0.01, 0.01];
  let items = [];
  let itemSelectedIndex = 0;

  const init = useCallback(() => {
    let myCanvas = document.getElementById("canvas");
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(70, window.innerWidth / window.innerHeight, 0.01, 20);

    const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
    light.position.set(0.5, 1, 0.25);
    scene.add(light);

    renderer = new THREE.WebGLRenderer({
      canvas: myCanvas,
      antialias: true,
      alpha: true,
    });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.xr.enabled = true;

    const xrLight = new XREstimatedLight(renderer);
    xrLight.addEventListener("estimationstart", () => {
      scene.add(xrLight);
      scene.remove(light);
      if (xrLight.environment) {
        scene.environment = xrLight.environment;
      }
    });

    xrLight.addEventListener("estimationend", () => {
      scene.add(light);
      scene.remove(xrLight);
    });

    let arButton = ARButton.createButton(renderer, {
      requiredFeatures: ["hit-test"],
      optionalFeatures: ["dom-overlay", "light-estimation"],
      domOverlay: { root: document.body },
    });
    arButton.style.bottom = "22%";
    document.body.appendChild(arButton);

    for (let i = 0; i < models.length; i++) {
      const loader = new GLTFLoader();
      loader.load(models[i], function (glb) {
        let model = glb.scene;
        items[i] = model;
      });
    }

    const controller = renderer.xr.getController(0);
    controller.addEventListener("select", onSelect);
    scene.add(controller);

    reticle = new THREE.Mesh(
      new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2),
      new THREE.MeshBasicMaterial()
    );
    reticle.matrixAutoUpdate = false;
    reticle.visible = false;
    scene.add(reticle);
  }, []);

  const onSelect = useCallback(() => {
    if (reticle.visible) {
      let newModel = items[itemSelectedIndex].clone();
      newModel.visible = true;
      reticle.matrix.decompose(newModel.position, newModel.quaternion, newModel.scale);
      let scaleFactor = modelScaleFactor[itemSelectedIndex];
      newModel.scale.set(scaleFactor, scaleFactor, scaleFactor);
      scene.add(newModel);
    }
  }, []);

  const setupFurnitureSelection = useCallback(() => {
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      if (el) {
        el.addEventListener("beforexrselect", (e) => {
          e.preventDefault();
          e.stopPropagation();
        });
        el.addEventListener("click", (e) => {
          e.preventDefault();
          e.stopPropagation();
          onClicked(e, items[i], i);
        });
      }
    }
  }, []);

  const onClicked = useCallback((e, selectItem, index) => {
    itemSelectedIndex = index;
    for (let i = 0; i < models.length; i++) {
      const el = document.querySelector(`#item` + i);
      if (el) el.classList.remove("clicked");
    }
    e.target.classList.add("clicked");
  }, []);

  const animate = useCallback(() => {
    renderer.setAnimationLoop(render);
  }, []);

  const render = useCallback((timestamp, frame) => {
    if (frame) {
      const referenceSpace = renderer.xr.getReferenceSpace();
      const session = renderer.xr.getSession();

      if (hitTestSourceRequested === false) {
        session.requestReferenceSpace("viewer").then(function (referenceSpace) {
          session
            .requestHitTestSource({ space: referenceSpace })
            .then(function (source) {
              hitTestSource = source;
            });
        });

        session.addEventListener("end", function () {
          hitTestSourceRequested = false;
          hitTestSource = null;
        });

        hitTestSourceRequested = true;
      }

      if (hitTestSource) {
        const hitTestResults = frame.getHitTestResults(hitTestSource);
        if (hitTestResults.length) {
          const hit = hitTestResults[0];
          reticle.visible = true;
          reticle.matrix.fromArray(hit.getPose(referenceSpace).transform.matrix);
        } else {
          reticle.visible = false;
        }
      }
    }
    renderer.render(scene, camera);
  }, []);

  useEffect(() => {
    init();
    setupFurnitureSelection();
    animate();

    const backButton = document.createElement('button');
    backButton.textContent = 'Back to Gallery';
    backButton.className = 'back-button';
    backButton.onclick = () => navigate('/furniture');
    document.body.appendChild(backButton);

    return () => {
      document.body.removeChild(backButton);
    };
  }, [init, setupFurnitureSelection, animate, navigate]);

  return (
    <div className="ar-view">
      <canvas id="canvas"></canvas>
      <div className="navbar">
        <div className="button-list">
          <div className="button-container">
            <img className="button-image" id="item0" src="/armchair.png" alt="Armchair" />
            <div className="item-label">Armchair</div>
          </div>
          <div className="button-container">
            <img className="button-image" id="item1" src="/lounger.png" alt="Lounger" />
            <div className="item-label">Lounger</div>
          </div>
          <div className="button-container">
            <img className="button-image" id="item2" src="/marble-coffeetable.png" alt="Marble Coffee Table" />
            <div className="item-label">Marble Coffee Table</div>
          </div>
          <div className="button-container">
            <img className="button-image" id="item3" src="/walnut-coffeetable.png" alt="Walnut Coffee Table" />
            <div className="item-label">Walnut Coffee Table</div>
          </div>
          <div className="button-container">
            <img className="button-image" id="item4" src="/chair-with-gold.png" alt="Gold Chair" />
            <div className="item-label">Gold Chair</div>
          </div>
          <div className="button-container">
            <img className="button-image" id="item5" src="/nesting-tables.png" alt="Nesting Tables" />
            <div className="item-label">Nesting Tables</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ARView;
